	public static String toFan(String text) {
		if (text == null || text.trim().equals(""))
			return text;
		String stReturn = "";
		for (int i = 0; i < text.length(); i++) {
			char temp = text.charAt(i);
			if (jtPy.indexOf(temp) != -1)
				stReturn += ftPy.charAt(jtPy.indexOf(temp));
			else
				stReturn += temp;
		}
		return stReturn;
	}

